public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int x=5;x>=0;x--)
		{
			for(int y=0;y<x;y++)
			{
				System.out.print("*");
			}
			System.out.println("");
		}
		for (int x=1;x<=5;x++)
		{
			for(int y=0;y<x;y++)
			{
				System.out.print("*");
			}
			System.out.println(" ");
		}
	}

}
