
public class MatrixArrayUse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [][] arry = {{1,2,3}, {2,3,4},{4,5,6}};
		for(int i=0; i< arry.length; i++)
		{
			for(int j=0; j<arry.length;j++)
			{
				System.out.print(arry[i][j]+ " ");
			}
		System.out.println();
		}
	}
}
