//Using sort method
import java.util.Arrays;

public class Sorting {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		{
		    int data[] = { 4, -5, 2, 6, 1 };
		 
		    Arrays.sort(data);
		 
		    for (int c: data) 
		    {
		      System.out.println(c);
		    }
		 }
    }
}
